import './contact.css';
import aboutText from '../../images/aboutText.svg';
//import {} from '../../components';
// import {Link} from 'react-router-dom';


export class Contact extends Component {
  constructor(props) {
   super(props);

   this.state = {
     name: '',
     email: '',
     subject: '',
     message: ''
    };
 }

 sendFormOff() {
  // const data = new FormData();
  // data.append('name', this.state.formFields.name);
  // data.append('email', this.state.formFields.email);
  // data.append('subject', this.state.formFields.subject);
  // data.append('message', this.state.formFields.message);

  fetch('/contactUs', {
    method: 'POST',
    body: {
      "name": this.state.name,
      "email": this.state.email,
      "subject": this.state.subject,
      "message": this.state.message
    },
  }).then((response) => {
    return console.log("show", response);
  })
  .catch((err) => {console.log('Error', err)} )
}

 // handleChange(e) {
 //   let formFields = {...this.state.formFields};
 //       formFields[e.target.name] = e.target.value;
 //     this.setState({
 //       formFields
 //     });
 //   };


handleChange = e => {
     this.setState({
       [e.target.name]: e.target.value
     });
   }


handleSubmit(e) {
 e.preventDefault();
 //clear form on submission
  this.setState({
    formSubmit: true,
    name: '',
    email: '',
    subject: '',
    message: '',
  });
  //send form off
  this.sendFormOff();
}


render() {

  return(
  <div className="body1">
    <h1 className="title1">Contact Me</h1>
    <div className="form">
      <form  onSubmit={e => this.handleSubmit(e)} >
           <textarea className="name" name="name" placeholder="Name" value={this.state.formFields.name} onChange={e => this.change(e)} required></textarea>
           <textarea className="email" name="email" placeholder="Email"  value={this.state.formFields.email} onChange={e => this.change(e)} required></textarea>
           <textarea className="subject" name="subject" placeholder="Subject" value={this.state.formFields.subject} onChange={e => this.change(e)} required></textarea>
           <textarea className="message" name="message" placeholder="Message" value={this.state.formFields.message} onChange={e => this.change(e)} required></textarea>
           <input type="submit" className="contactSubmit" value="Send" ></input>
        </form>
      </div>
  </div>

    );
  }
}




export default Contact;
