import React, { useState } from 'react';
import styled from 'styled-components';
import one from '../../../images/new/colorplug.png';
import ImageScroller from 'react-image-scroller';


const Wrapper = styled.div`
.hide {
  display: none;
}
.show {
background-color: pink;
height: 90%;
width: 100%;
position: absolute;
display: inline-block;
top: 50%;
left: 50%;
transform: translate(-50%,-50%);
z-index: 1;
}
.index-buttons {
  width: 100%;
    display: flex;
    margin: 1em;
    flex-wrap: wrap;
    justify-content: center;
    pointer-events: none;
    position: absolute;
    bottom: 0;
    .index-button {
      padding: 0.5em;
    border-radius: 50%;
    }
}
`;

const Content = styled.div`

`;

const Close = styled.button`
width: 20px;
height: 20px;
background-color: grey;
float: right;
margin: 10px 10px;
`;


// position: fixed;
// overflow-y: scroll;
// height: 100%;
// background: url('${long}');

const show = (pictures) => {
  pictures.map(
    pic => {return <img src={pic.url} alt={pic.page} />}
  )
};

const Showcase = (work) => {
  const [close, setClose] = useState(false);
  console.log('show me', work.work);
  return (
  <Wrapper>
    <Content className={close ? 'hide': 'show' }>
        {
          show(work.work)
        }
      <ImageScroller style={{width: 'inherit', height: '100%'}}
        hideScrollbar={false}
      >
        <img src={one} alt="First" />
        <img src={one} alt="Second" />
        <img src={one} alt="Third" />
        <img src={one} alt="Fourth" />
        <img src={one} alt="Fifth" />
      </ImageScroller>
      <Close onClick={() => setClose(true)}></Close>
    </Content>
  </Wrapper>
)
}



export default Showcase;
