import React, { Component } from 'react';
import './about.scss';
import pdf from '../../images/Kahti Demba CV.pdf';





export class About extends Component {
  constructor(props) {
   super(props);

   this.state = {

   }
 }



render() {
  return(
      <div className={ this.props.modeChange ? "dark_body" : "body"}>
        <div className="paragraph">
            <div id="p" className="aboutText">
              <h1 style={{color: "#00EBFF"}}>Hi, I'm Kahti N Demba</h1>
              <span style={{color: "#00FFC4"}}>a UX designer and Interactive developer based in London, currently working at IBM iX.</span>
              <span style={{color: "#00FF80"}}>I aim to solve complex user centric problems, and aide in digital transformation strategies by leveraging a multidisciplinary digital skill set and academic marketing background.</span>
              <span style={{color: "#76FF00"}}>A tribute to my role at iX, I have been fortunate enough to work with the listed brands below, using IBM Garage in order to drive digital transformations in their businesses.</span>
              <span style={{color: "#FFCE00"}}>My proficiencies lie in; &nbsp;</span>
              <span style={{color: "#FF9D00"}}>Adobe Illustrator, Photoshop, XD, HTML5, CSS3, Vanilla Javascript, React.js & Node.js</span>
            </div>
            <a className="CV" target="_blank" rel='noreferrer noopener' href={pdf}>View my CV</a>
            <a className="Link" target="_blank" rel='noreferrer noopener' href="https://www.linkedin.com/in/kahti-demba-79501a138/">LinkedIn</a>
        </div>
      </div>
    );
  }

}





export default About;
