import React, { Component } from 'react';
import './portfolio.css';
import aboutText from '../../images/aboutText.svg';

import logo1 from '../../images/cp.png';
import logoMU1 from '../../images/cpMockUp.png';

import logo2 from '../../images/flj.png';
import logoMU2 from '../../images/fljMockUp.png';

import logo3 from '../../images/bgnr.png';
import logoMU3 from '../../images/bgnrMockUp.png';

// import logo4 from '../../images/aboutText.svg';
// import logoMU4 from '../../images/aboutText.svg';
//
// import logo5 from '../../images/aboutText.svg';
// import logoMU5 from '../../images/aboutText.svg';
//
// import logo6 from '../../images/aboutText.svg';
// import logoMU6 from '../../images/aboutText.svg';


export class Portfolio extends Component {
  constructor() {
    super();
    this.state = {
      clicked: false,
      change: false
    };
  }


  click1 = () => {
    this.setState({
      clicked1: true,
    });
  }

  unclick1 = () => {
    this.setState({
      clicked1: false,
    });
  }

  next1 = () => {
    this.setState({
      change2: true,
    })
  }

  previous1 = () => {
    this.setState({
      change2: false,
    })
  }

  click2 = () => {
    this.setState({
      clicked2: true,
    });
  }

  unclick2 = () => {
    this.setState({
      clicked2: false,
    });
  }

  next2 = () => {
    this.setState({
      change2: true,
    })
  }

  previous2 = () => {
    this.setState({
      change2: false,
    })
  }

  click3 = () => {
    this.setState({
      clicked3: true,
    });
  }

  unclick3 = () => {
    this.setState({
      clicked3: false,
    });
  }

  next3 = () => {
    this.setState({
      change3: true,
    })
  }

  previous3 = () => {
    this.setState({
      change3: false,
    })
  }

  click4 = () => {
    this.setState({
      clicked4: true,
    });
  }

  unclick4 = () => {
    this.setState({
      clicked4: false,
    });
  }

  next4 = () => {
    this.setState({
      change4: true,
    })
  }

  previous4 = () => {
    this.setState({
      change4: false,
    })
  }

  click5 = () => {
    this.setState({
      clicked5: true,
    });
  }

  unclick5 = () => {
    this.setState({
      clicked5: false,
    });
  }

  next5 = () => {
    this.setState({
      change5: true,
    })
  }

  previous5 = () => {
    this.setState({
      change5: false,
    })
  }

  click6 = () => {
    this.setState({
      clicked6: true,
    });
  }

  unclick6 = () => {
    this.setState({
      clicked6: false,
    });
  }

  next6 = () => {
    this.setState({
      change6: true,
    })
  }

  previous6 = () => {
    this.setState({
      change6: false,
    })
  }

render() {
  console.log("which one is it", this.state.clicked);
  return(
  <div className="body">
    <div className={this.state.clicked ? "gallery" : "hide"}>
      <div className="top-row">
        <div className="one" style={{backgroundImage: `url(${logo1})`}} onClick={this.click.bind(this)} ></div>
        <div className="two" style={{backgroundImage: `url(${logo2})`}}></div>
        <div className="three" style={{backgroundImage: `url(${logo3})`}}></div>
      </div>
      <div className="bottom-row">
        <div className="four" style={{backgroundImage: `url(${logo1})`}}></div>
        <div className="five" style={{backgroundImage: `url(${logo2})`}}></div>
        <div className="six" style={{backgroundImage: `url(${logo3})`}}></div>
      </div>
    </div>
    <h1 className="title">Previous Work</h1>

    { this.state.clicked1 &&
        <div className="galleryView">
          <img src={this.state.change1 ? logo1 :logoMU1} width="100%" height="100%" onClick={this.unclick1.bind(this)}></img>
          <button className="next" onClick={this.next1.bind(this)}>Next</button>
          <button className="next" onClick={this.previous1.bind(this)}>Previous</button>
        </div>
    }

    { this.state.clicked2 &&
        <div className="galleryView">
          <img src={this.state.change2 ? logo2 :logoMU2} width="100%" height="100%" onClick={this.unclick2.bind(this)}></img>
          <button className="next" onClick={this.next2.bind(this)}>Next</button>
          <button className="next" onClick={this.previous2.bind(this)}>Previous</button>
        </div>
    }

    { this.state.clicked3 &&
        <div className="galleryView">
          <img src={this.state.change3 ? logo3 :logoMU3} width="100%" height="100%" onClick={this.unclick3.bind(this)}></img>
          <button className="next" onClick={this.next3.bind(this)}>Next</button>
          <button className="next" onClick={this.previous3.bind(this)}>Previous</button>
        </div>
    }

    { this.state.clicked4 &&
        <div className="galleryView">
          <img src={this.state.change4 ? logo1 :logoMU1} width="100%" height="100%" onClick={this.unclick4.bind(this)}></img>
          <button className="next" onClick={this.next4.bind(this)}>Next</button>
          <button className="next" onClick={this.previous4.bind(this)}>Previous</button>
        </div>
    }

    { this.state.clicked5 &&
        <div className="galleryView">
          <img src={this.state.change5 ? logo2 :logoMU2} width="100%" height="100%" onClick={this.unclick5.bind(this)}></img>
          <button className="next" onClick={this.next5.bind(this)}>Next</button>
          <button className="next" onClick={this.previous5.bind(this)}>Previous</button>
        </div>
    }

    { this.state.clicked6 &&
        <div className="galleryView">
          <img src={this.state.change6 ? logo3 :logoMU3} width="100%" height="100%" onClick={this.unclick6.bind(this)}></img>
          <button className="next" onClick={this.next6.bind(this)}>Next</button>
          <button className="next" onClick={this.previous6.bind(this)}>Previous</button>
        </div>
    }

  </div>
    );
  }
}




export default Portfolio;
