export class Contact extends Component {
  constructor(props) {
   super(props);

   this.state = {

   }
 }

 handleFormClear = (e) => {
 e.preventDefault();
 this.setState({
   formFields: { name: '', email: '', subject: '', message: '' },
   formSubmit: false,
 });
}

 sendFormOff() {
   const data = new FormData();
   data.append('file', this.state.fileValue );
   data.append('name', this.state.formFields.name);
   data.append('email', this.state.formFields.email);
   data.append('subject', this.state.formFields.subject);
   data.append('message', this.state.formFields.message);

   fetch('/contactUs', {
     method: 'POST',
     body: data,
   }).then((response) => {
           console.log('ResponseLog', response)
           // this.setState({formSubmit: true})
   })
   .catch((err) => {console.log('Error', err)} )
 }

 handleSubmit(e) {
       e.preventDefault();

         this.handleFormClear(e);
         this.setState({ formSubmit: true });
         this.sendFormOff();
 }

render() {

  return(
  <div className="body1">
    <h1 className="title1">Contact Me</h1>
    <div className="form">
      <form  onSubmit={e => this.handleSubmit(e)} encType='multipart/form-data'>
           <textarea className="name" name="name" placeholder="Name" required></textarea>
           <textarea className="email" name="email" placeholder="Email"  required></textarea>
           <textarea className="subject" name="subject" placeholder="Subject" required></textarea>
           <textarea className="message" name="message" placeholder="Message" required></textarea>
           <input type="submit" className="contactSubmit" value="Send" ></input>
        </form>
      </div>
  </div>

    );
  }
}




export default Contact;
