import React, { useState } from 'react';
import styled from 'styled-components';
import ImageScroller from 'react-image-scroller';


const Wrapper = styled.div`
height: 100%
width: 100%;

.hide {
  display: none;
}
.show {
  height: 100%;
  width: 100%;
  position: absolute;
  display: inline-block;
  top: 50%;
  left: 50%;
  transform: translate(-50%,-50%);
  z-index: 1;
  background-color: white;
}
`;

const Content = styled.div`

`;


const one = 'https://drive.google.com/uc?id=1hLw0Ap1_eekf9vv32uz6vDj9PDvw1j2z';
const two = 'https://drive.google.com/uc?id=1KBDVqnC8RX0IwvQlE4OUVSHbu3fo0YLu';

const Showcase6 = (work) => {
  const [close, setClose] = useState(false);
  return (
  <Wrapper>
    <Content className={close ? 'hide': 'show' }>
      <button onClick={() => setClose(true)} >Tap here or white space to close</button>
      <ImageScroller style={{width: 'inherit', height: 'inherit'}}
        hideScrollbar={false}
      >
        <img src={one} alt="First" />
        <img src={two} alt="Second" />
      </ImageScroller>
    </Content>
  </Wrapper>
)
}



export default Showcase6;
