import React, { Component } from 'react';
import './about.scss';
import pdf from '../../images/Kahti Demba CV.pdf';





export class About extends Component {
  constructor(props) {
   super(props);

   this.state = {

   }
 }



render() {
  return(
      <div className={ this.props.modeChange ? "dark_body" : "body"}>
        <div className="paragraph">
            <div id="p" className="aboutText">
              <h1 style={{color: "#00EBFF"}}>Hi, I'm Kahti N Demba</h1>
              <p style={{color: "#00FFC4"}}>a UX designer and Interactive developer based in London, currently working at IBM iX.</p>
              <p style={{color: "#00FF80"}}>I aim to solve complex user centric problems, and aide in digital transformation strategies by leveraging a multidisciplinary digital skill set and academic marketing background.</p>
              <p style={{color: "#76FF00"}}>More insight regarding my skill set can be found through the viewing of;</p>
              <p style={{color: "#FFCE00"}}>My CV</p>
              <p style={{color: "#FF9D00"}}>My LinkedIn</p>
              <p>I also invite you to view a select few of my personal projects produced outside of my full time role, to be found on the left. </p>
              <p>Thank you for taking the time to visit, please do not hesitate to contact me at: <a href="/">kahti.demba@hotmail.co.uk</a> </p>
            </div>
            <a className="CV" target="_blank" rel='noreferrer noopener' href={pdf}>View my CV</a>
            <a className="Link" target="_blank" rel='noreferrer noopener' href="https://www.linkedin.com/in/kahti-demba-79501a138/">LinkedIn</a>
        </div>
      </div>
    );
  }

}





export default About;
