import React, { Component } from 'react';
import './portfolio.css';
import aboutText from '../../images/aboutText.svg';

import logo1 from '../../images/cp.png';
import logoMU1 from '../../images/cpMockUp.png';

import logo2 from '../../images/flj.png';
import logoMU2 from '../../images/fljMockUp.png';

import logo3 from '../../images/bgnr.png';
import logoMU3 from '../../images/bgnrMockUp.png';

// import logo4 from '../../images/aboutText.svg';
// import logoMU4 from '../../images/aboutText.svg';
//
// import logo5 from '../../images/aboutText.svg';
// import logoMU5 from '../../images/aboutText.svg';
//
// import logo6 from '../../images/aboutText.svg';
// import logoMU6 from '../../images/aboutText.svg';

const galleryView = {
  height: '750px',
  width: '650px',
  margin: '0 auto',
  // backgroundImage: `url(${logo1})`,
  // backgroundPosition: 'center',
  // backgroundRepeat: 'no-repeat',
  // backgroundSize: 'auto',
  position: 'relative',
  bottom: '850px'
}

//import {} from '../../components';
// import {Link} from 'react-router-dom';

export class Portfolio extends Component {
  constructor() {
    super();
    this.state = {
      nextImage: false,
      clicked: false,
      change: false
    };
  }

  nextImagePop = () => {
    this.setState({
      nextImage: !this.state.nextImage
    })
  }

  click = () => {
    this.setState({
      clicked: true,
    });
  }

  unclick = () => {
    this.setState({
      clicked: false,
    });
  }

  next = () => {
    this.setState({
      change: true,
    })
  }

  previous = () => {
    this.setState({
      change: false,
    })
  }


render() {
  console.log("which one is it", this.state.clicked);
  return(
  <div className="body">
    <div className={this.state.clicked ?"gallery" : "hide"}>
      <div className="top-row">
        <div className="one" style={{backgroundImage: `url(${logo1})`}} onClick={this.click.bind(this)} ></div>
        <div className="two" style={{backgroundImage: `url(${logo2})`}}></div>
        <div className="three" style={{backgroundImage: `url(${logo3})`}}></div>
      </div>
      <div className="bottom-row">
        <div className="four" style={{backgroundImage: `url(${logo1})`}}></div>
        <div className="five" style={{backgroundImage: `url(${logo2})`}}></div>
        <div className="six" style={{backgroundImage: `url(${logo3})`}}></div>
      </div>
    </div>
    <h1 className="title">Previous Work</h1>

    { this.state.clicked &&
        <div className="galleryView">
          <img src={this.state.change ? logo1 :logoMU1} onClick={this.unclick.bind(this)}></img>
          <button className="next" onClick={this.next.bind(this)}>Next</button>
          <button className="next" onClick={this.previous.bind(this)}>Previous</button>
        </div>
    }

  </div>
    );
  }
}




export default Portfolio;
