import React, { Component } from 'react';
import './portfolio.scss';
import Showcase from './Showcase';

// import logo1 from '../../images/new/Brown Paper Bag MockUp.jpg';
// import logoMU1 from '../../images/new/Brown Paper Bag MockUp.jpg';
//
// import logo2 from '../../images/new/colorplug.png';
// import logoMU2 from '../../images/new/colorplug.png';
//
// import logo3 from '../../images/new/Indstry.jpg';
// import logoMU3 from '../../images/new/Indstry.jpg';
//
// import placeholder from '../../images/default.svg';
// import dark_placeholder from '../../images/dark_default.svg';



export class Portfolio extends Component {
  constructor() {
    super();
    this.state = {
      clicked: false,
      clicked2: false,
      clicked3: false,
      clicked4: false,
      clicked5: false,
      clicked6: false,
    };
  }

  click = () => {
    this.setState({
      clicked: !this.state.clicked,
    });
  }
  click2 = () => {
    this.setState({
      clicked2: !this.state.clicked2,
    });
  }
  click3 = () => {
    this.setState({
      clicked3: !this.state.clicked3,
    });
  }
  click4 = () => {
    this.setState({
      clicked4: !this.state.clicked4,
    });
  }
  click5 = () => {
    this.setState({
      clicked5: !this.state.clicked5,
    });
  }
  click6 = () => {
    this.setState({
      clicked6: !this.state.clicked6,
    });
  }

render() {
  return(
  <div className={this.props.modeChange ? "dark_body" : "body"}>
    <div className="sizing">
      {
        this.state.clicked &&
        <Showcase style={{position: 'absolute', display: 'inline-block'}}/>
      }
      {
        this.state.clicked2 &&
        <Showcase style={{position: 'absolute', display: 'inline-block'}}/>
      }
      {
        this.state.clicked3 &&
        <Showcase style={{position: 'absolute', display: 'inline-block'}}/>
      }
      {
        this.state.clicked4 &&
        <Showcase style={{position: 'absolute', display: 'inline-block'}}/>
      }
      {
        this.state.clicked5 &&
        <Showcase style={{position: 'absolute', display: 'inline-block'}}/>
      }
      {
        this.state.clicked6 &&
        <Showcase style={{position: 'absolute', display: 'inline-block'}}/>
      }
      <div className="gallery">

          <div id="one" className="one" style={{backgroundImage: `url(../../images/new/colorplug.png)`}} onClick={this.click.bind(this)} ></div>
          <div id="two" className="two" style={{backgroundImage: `url(../../images/new/colorplug.png)`}} onClick={this.click2.bind(this)}></div>
          <div id="three" className="three" style={{backgroundImage: `url(../../images/new/colorplug.png)`}} onClick={this.click3.bind(this)}></div>


          <div id="four" className="four" style={{backgroundImage: `url('../../images/new/colorplug.png')`}} onClick={this.click4.bind(this)}></div>
          <div id="five" className="five" style={{backgroundImage: `url('../../images/new/colorplug.png')`}} onClick={this.click5.bind(this)}></div>
          <div id="six" className="six" style={{backgroundImage: `url('../../images/new/colorplug.png')`}} onClick={this.click6.bind(this)}></div>

          <h1 className="title">Previous Work</h1>

      </div>
    </div>
  </div>
    );
  }
}




export default Portfolio;
