import React, { useState } from 'react';
import styled from 'styled-components';
import ImageScroller from 'react-image-scroller';


const Wrapper = styled.div`
&&&.hide {
  display: none !important;
}
/* height: 100%; */
width: 100%;
position: absolute;
/* display: inline-block; */
/* top: 50%; */
/* left: 50%; */
/* transform: translate(-50%,-50%); */
z-index: 1;
background-color: white;
margin-top: 20vh;
`;

const one = 'https://drive.google.com/uc?id=1vcW_HVz5fn3oMhvBtzVbkRrSSPAu2VId';
const two = 'https://drive.google.com/uc?id=1Z9HYImhzUHFL7X6LrRZPAhFl-fCBQjDq';

const Showcase5 = (work) => {
  const [close, setClose] = useState(false);
  return (
  <Wrapper className={close && 'hide'}>
        <button onClick={() => setClose(true)}>Close</button>
        <ImageScroller style={{width: 'inherit', maxHeight: 'inherit'}}
          hideScrollbar={false}
        >
          <img src={one} alt="First" />
          <img src={two} alt="Second" />
        </ImageScroller>
  </Wrapper>
)
}



export default Showcase5;
