import React, { useState } from 'react';
import light_logo from './images/logo.svg';
import dark_logo from './images/darklogo.svg';
import {About, Portfolio, Contact} from './components';
import './App.scss';
import Zoom from 'react-reveal/Zoom';
import ReactFullpage from "@fullpage/react-fullpage";









function App() {

  const [mode, setMode] = useState(false);

  return (

      <ReactFullpage
        licenseKey={'your key'}
        render={({ state, fullpageApi }) => {

          return (
              <div>
                    <div className="section">
                      <Zoom>
                          <header className="header">
                            <button onClick={() => setMode(prevMode => !prevMode)}>change mode</button>
                            <div className="logo">
                              <img src={light_logo}  alt="logo" />
                            </div>
                            <footer>Kahti N Demba &copy; 2019</footer>
                          </header>
                      </Zoom>
                    </div>

                  <div className="section">
                    <Zoom>
                      <div className="section">
                        <About/>
                        </div>
                    </Zoom>
                  </div>

                  <div className="section">
                    <Zoom>
                      <div className="section">
                        <Portfolio/>
                      </div>
                    </Zoom>
                  </div>

                  <div className="section">
                    <Zoom>
                      <div className="section">
                        <Contact/>
                      </div>
                    </Zoom>
                  </div>
            </div>
          );
        }}
      />
  );
}





export default App;
